/* at45x buffer fill */static void at45_buffer_fill(unsigned id, const void *in) {	unsigned char buffer[AT45_PAGE_SIZE], req[4];    unsigned addr, first_data_byte = 0;        addr = (first_data_byte << 13);    	req[0] = (id == 1) ? 0x84 : 0x87;	req[1] = ((addr & 0x00ff0000) >> 16);	req[2] = ((addr & 0x0000ff00) >> 8);	req[3] = (addr & 0x000000ff);	util_memcp(buffer, in, AT45_PAGE_SIZE);    	at45_transfer(req, 4, buffer, AT45_PAGE_SIZE);}