/* at45x initing */extern unsigned at45_init(void) {    unsigned char req[4];        /* Get device information */    req[0] = 0x9f;        at45_transfer(req, sizeof(req), 0, 0);        util_printf("Manufacturer ID: %x\n", req[1]);    util_printf("Density Code: %x\n", req[2]);    util_printf("Product Version: %x\n", req[3]);        if (req[1] != 0x1f || req[2] != 0x28) {		util_printf("Unknown Device\n");		return -1;	}    else {        util_printf("Device is AT45DB642D\n");    }	util_printf("Status Register: %x\n", at45_status());	return 0;}