/* at45x continius reading data */extern unsigned at45_read_cont(unsigned lba, void *ptr, unsigned len) {    unsigned char req[8];	unsigned addr, page, byte;        page = lba / AT45_PAGE_SIZE;    byte = lba % AT45_PAGE_SIZE;	addr = (page << 11) + byte;	util_printf("\nReading page = 0x%x, byte = 0x%x addr = 0x%x, length = 0x%x\n",			page, byte, addr, len);	req[0] = 0xe8;	req[1] = ((addr & 0x00ff0000) >> 16);	req[2] = ((addr & 0x0000ff00) >> 8);	req[3] = ((addr & 0x000000ff));    req[4] = 0x0;    req[5] = 0x0;    req[6] = 0x0;    req[7] = 0x0;    	at45_transfer(req, 8, ptr, len);    	return 0;}/* at45x reading data */extern unsigned at45_read(unsigned lba, void *ptr, unsigned len) {	unsigned page, pg_n, z0, z1;    /* check data address and size */    page = lba / AT45_PAGE_SIZE;    z0 = lba % AT45_PAGE_SIZE;        pg_n = len / AT45_PAGE_SIZE;    z1 = len % AT45_PAGE_SIZE;	if (z0 != 0)		return -1;	if (z1 != 0)		return -1;        util_printf("Reading page = 0x%x, count = 0x%x\n",			page, pg_n);        while (pg_n--) {        at45_wait_rdy();        		at45_page_read(lba, ptr);		ptr += AT45_PAGE_SIZE;        lba += AT45_PAGE_SIZE;	}	at45_wait_rdy();    return 0;}